import { Edge } from "@ali/flow-infra-g";
export default class BizEdge extends Edge {
  constructor() {
    super(...arguments);
    this.bazier = true;
  }

  onMount() {
    this.setData({
      label: "自定义"
    });
  }

  labelFormatter(label) {
    const {
      source,
      target
    } = this.getLinkNodesData();
    if (!source || !target) return "正在创建连线";
    return label + String(source.x + target.x);
  }

